definition.script:
parallel windows:
{
    node("windows && i386 && amd64")
    {
        timeout(10)
        {
            stage("Checkout (windows)")
            {
                checkout([$class: 'GitSCM', branches: [[name: '*/master']], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], gitTool: 'jgit', submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: 'https://github.com/cmake-maven-project/cmake-maven-project.git']]])
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin;${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (windows-i386)")
                {
                    // https://jenkins.io/doc/pipeline/examples/
                    // Apache Maven related side notes:
                    // --batch-mode : recommended in CI to inform maven to not run in interactive mode (less logs)
                    // -V : strongly recommended in CI, will display the JDK and Maven versions in use.
                    //      Very useful to be quickly sure the selected versions were the ones you think.
                    // -U : force maven to update snapshots each time (default : once an hour, makes no sense in CI).
                    // -Dsurefire.useFile=false : useful in CI. Displays test errors in the logs directly (instead of
                    //                            having to crawl the workspace files to see the cause).            
                    bat script: 'mvn --batch-mode -V -U -e clean deploy -Pwindows-x86_32 -Dsurefire.useFile=false'
                    archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                }
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin;${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (windows-amd64)")
                {
                    bat script: 'mvn --batch-mode -V -U -e clean deploy -Pwindows-x86_64 -Dsurefire.useFile=false'
                    archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                }
            }
        }
    }
}, linux_i386:
{
    node("linux && i386")
    {
        timeout(10)
        {
            stage("Checkout (linux-i386)")
            {
                checkout([$class: 'GitSCM', branches: [[name: '*/master']], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'MessageExclusion', excludedMessage: '^\\[jenkins\\]']], gitTool: 'jgit', submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: 'https://github.com/cmake-maven-project/cmake-maven-project.git']]])
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA32_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (linux-i386)")
                {
                    sh script: '''sudo apt-get install cmake -y
                    mvn --batch-mode -V -U -e clean deploy -Plinux-x86_32 -Dsurefire.useFile=false'''
                    archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                }
            }
        }
    }
}, linux_amd64:
{
    node("linux && amd64")
    {
        timeout(10)
        {
