            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
            {
                stage("Deploy (linux-amd64)")
                {
                    sh script: 'mvn --batch-mode -V -U -e clean deploy -Plinux-x86_64 -Dsurefire.useFile=false'
                    archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                }
            }
        }
    }
}, mac:
{
    node("mac")
    {
        timeout(10)
        {
            stage("Checkout (mac)")
            {
                    checkout([$class: 'GitSCM', branches: [[name: '*/master']], browser: [$class: 'GithubWeb', repoUrl: 'https://github.com/cmake-maven-project/cmake-maven-project/'], doGenerateSubmoduleConfigurations: false, extensions: [], gitTool: 'jgit', submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'github', url: 'https://github.com/cmake-maven-project/cmake-maven-project.git']]])
            }
        }
        timeout(15)
        {
            withEnv(["JAVA_HOME=${ tool 'JAVA64_HOME' }", "PATH+MAVEN=${tool 'Maven'}/bin:${env.JAVA_HOME}/bin"])
