                    sh script: 'mvn --batch-mode -V -U -e clean deploy -Pmac-x86_64 -Dsurefire.useFile=false'
                    archiveArtifacts artifacts: '**/target/*.jar', excludes: '**/target/test-classes/**'
                }
            }
        }
    }
},
failFast: true
